import React, { useState, useEffect } from 'react';
import { Typography, Box, CircularProgress, Paper } from '@mui/material';
import { useAuth } from '../../contexts/AuthContext';
import { authService } from '../../services/auth';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const { user } = useAuth();
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user || !user._id) {
      // If no user is found in context, redirect to login
      navigate('/login');
      return;
    }
    const fetchUserDetails = async () => {
      try {
        const res = await authService.getUserDetails(user._id);
        if (res.data && res.data.user) {
          setUserDetails(res.data.user);
        }
      } catch (error) {
        console.error("Error fetching user details:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserDetails();
  }, [user, navigate]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!userDetails) {
    return (
      <Typography variant="h6" align="center" sx={{ mt: 4 }}>
        No user details available.
      </Typography>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Welcome, {userDetails.username}
      </Typography>
      <Paper sx={{ p: 2 }}>
        <Typography variant="subtitle1" gutterBottom>
          Bio:
        </Typography>
        <Typography variant="body1" gutterBottom>
          {userDetails.bio || 'No bio available'}
        </Typography>

        <Typography variant="subtitle1" gutterBottom>
          Interests:
        </Typography>
        <Typography variant="body1" gutterBottom>
          {userDetails.interests || 'Not specified'}
        </Typography>

        <Typography variant="subtitle1" gutterBottom>
          Likes:
        </Typography>
        <Typography variant="body1" gutterBottom>
          {userDetails.likes || 'Not specified'}
        </Typography>

        <Typography variant="subtitle1" gutterBottom>
          Dislikes:
        </Typography>
        <Typography variant="body1" gutterBottom>
          {userDetails.dislikes || 'Not specified'}
        </Typography>

        <Typography variant="subtitle1" gutterBottom>
          Preferences:
        </Typography>
        <Typography variant="body1">
          {userDetails.preferences || 'Not specified'}
        </Typography>
      </Paper>
    </Box>
  );
}
