import React, { useState, useEffect } from 'react';
import { 
  Typography, 
  Box, 
  CircularProgress, 
  Card, 
  CardContent, 
  Avatar, 
  IconButton, 
  Dialog, 
  DialogTitle, 
  DialogContent, 
  DialogActions, 
  Button, 
  TextField 
} from '@mui/material';
import SettingsIcon from '@mui/icons-material/Settings';
import { useAuth } from '../../contexts/AuthContext';
import { authService } from '../../services/auth';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const { user, login } = useAuth();
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Settings dialog state
  const [openSettings, setOpenSettings] = useState(false);
  const [formUsername, setFormUsername] = useState('');
  const [formBio, setFormBio] = useState('');
  const [formInterests, setFormInterests] = useState('');
  const [formLikes, setFormLikes] = useState('');
  const [formDislikes, setFormDislikes] = useState('');
  const [formPreferences, setFormPreferences] = useState('');
  const [saving, setSaving] = useState(false);
  const [settingsError, setSettingsError] = useState('');

  useEffect(() => {
    if (!user || !user._id) {
      navigate('/login');
      return;
    }
    const fetchUserDetails = async () => {
      try {
        const res = await authService.getUserDetails(user._id);
        if (res.data && res.data.user) {
          setUserDetails(res.data.user);
        }
      } catch (error) {
        console.error("Error fetching user details:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchUserDetails();
  }, [user, navigate]);

  const handleOpenSettings = () => {
    // Pre-fill fields with current details
    setFormUsername(userDetails.username || '');
    setFormBio(userDetails.bio || '');
    setFormInterests(
      Array.isArray(userDetails.interests) 
        ? userDetails.interests.join(', ') 
        : userDetails.interests || ''
    );
    setFormLikes(
      Array.isArray(userDetails.likes)
        ? userDetails.likes.join(', ')
        : userDetails.likes || ''
    );
    setFormDislikes(
      Array.isArray(userDetails.dislikes)
        ? userDetails.dislikes.join(', ')
        : userDetails.dislikes || ''
    );
    const prefs = userDetails.preferences || {};
    setFormPreferences(`Match Preference: ${prefs.matchPreference || ''}, Anonymous Chat: ${prefs.anonymousChat ? 'Yes' : 'No'}`);
    setSettingsError('');
    setOpenSettings(true);
  };

  const handleCloseSettings = () => {
    setOpenSettings(false);
  };

  // Helper functions to parse preferences from the form string.
  const extractMatchPreference = (prefStr) => {
    const match = prefStr.match(/Match Preference:\s*([^,]+)/i);
    return match ? match[1].trim() : '';
  };

  const extractAnonymousChat = (prefStr) => {
    const match = prefStr.match(/Anonymous Chat:\s*(Yes|No)/i);
    return match ? (match[1].toLowerCase() === 'yes') : false;
  };

  const handleSettingsSave = async () => {
    setSaving(true);
    setSettingsError('');
    try {
      let newToken = null;
      // If username changed, update it and capture the new token.
      if (formUsername !== userDetails.username && formUsername.trim() !== "") {
        const resUsername = await authService.changeUsername(formUsername);
        newToken = resUsername.data.token; // new token from changeUsername
        login(newToken, resUsername.data.user);
        setUserDetails(resUsername.data.user);
      }
      // Prepare updated details for the rest of the profile.
      const updateData = {
        bio: formBio,
        interests: formInterests.split(',').map(item => item.trim()).filter(item => item),
        likes: formLikes.split(',').map(item => item.trim()).filter(item => item),
        dislikes: formDislikes.split(',').map(item => item.trim()).filter(item => item),
        preferences: {
          matchPreference: extractMatchPreference(formPreferences),
          anonymousChat: extractAnonymousChat(formPreferences)
        }
      };
      const resDetails = await authService.updateUserDetails(updateData);
      if (resDetails.data && resDetails.data.user) {
        // Use token from updateUserDetails if provided, else use newToken
        newToken = resDetails.data.token || newToken;
        login(newToken, resDetails.data.user);
        setUserDetails(resDetails.data.user);
      }
      setOpenSettings(false);
    } catch (err) {
      setSettingsError(err.response?.data?.message || 'Profile update failed');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!userDetails) {
    return (
      <Typography variant="h6" align="center" sx={{ mt: 4 }}>
        No user details available.
      </Typography>
    );
  }

  return (
    <Box sx={{ p: 3, maxWidth: 800, mx: 'auto', backgroundColor: '#f3f6f9', borderRadius: 2 }}>
      <Card elevation={6} sx={{ borderRadius: '16px', p: 3, position: 'relative', backgroundColor: '#fff' }}>
        <IconButton 
          onClick={handleOpenSettings}
          sx={{ position: 'absolute', top: 16, right: 16 }}
          aria-label="settings"
        >
          <SettingsIcon fontSize="large" />
        </IconButton>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
            <Avatar 
              src={userDetails.profilePic || '/default-avatar.png'} 
              sx={{ width: 100, height: 100, mr: 3, border: '4px solid #1976d2' }}
            >
              {userDetails.username.charAt(0).toUpperCase()}
            </Avatar>
            <Typography variant="h3" sx={{ fontWeight: 600, color: '#333' }}>
              Welcome, {userDetails.username}!
            </Typography>
          </Box>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Bio
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              {userDetails.bio || 'No bio available.'}
            </Typography>
          </Box>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Interests
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              {userDetails.interests && Array.isArray(userDetails.interests)
                ? userDetails.interests.join(' â€¢ ')
                : userDetails.interests || 'Not specified.'}
            </Typography>
          </Box>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Likes
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              {userDetails.likes && Array.isArray(userDetails.likes)
                ? userDetails.likes.join(' â€¢ ')
                : userDetails.likes || 'Not specified.'}
            </Typography>
          </Box>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Dislikes
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              {userDetails.dislikes && Array.isArray(userDetails.dislikes)
                ? userDetails.dislikes.join(' â€¢ ')
                : userDetails.dislikes || 'Not specified.'}
            </Typography>
          </Box>
          <Box>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Preferences
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              Anonymous Chat: {userDetails.preferences?.anonymousChat ? "Yes" : "No"} <br />
              Match Preference: {userDetails.preferences?.matchPreference || 'Not specified.'}
            </Typography>
          </Box>
        </CardContent>
      </Card>

      <Dialog open={openSettings} onClose={handleCloseSettings} fullWidth maxWidth="sm">
        <DialogTitle>Update Profile</DialogTitle>
        <DialogContent>
          {settingsError && (
            <Typography color="error" variant="body2" sx={{ mb: 2 }}>
              {settingsError}
            </Typography>
          )}
          <TextField
            margin="dense"
            label="Username"
            fullWidth
            value={formUsername}
            onChange={(e) => setFormUsername(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Bio"
            fullWidth
            multiline
            rows={3}
            value={formBio}
            onChange={(e) => setFormBio(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Interests (comma separated)"
            fullWidth
            value={formInterests}
            onChange={(e) => setFormInterests(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Likes (comma separated)"
            fullWidth
            value={formLikes}
            onChange={(e) => setFormLikes(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Dislikes (comma separated)"
            fullWidth
            value={formDislikes}
            onChange={(e) => setFormDislikes(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Preferences (Format: Match Preference: <value>, Anonymous Chat: Yes/No)"
            fullWidth
            value={formPreferences}
            onChange={(e) => setFormPreferences(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseSettings} disabled={saving}>Cancel</Button>
          <Button onClick={handleSettingsSave} disabled={saving}>
            {saving ? <CircularProgress size={24} /> : 'Save'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
