import React, { useEffect, useState } from 'react';
import { Box, Typography } from '@mui/material';
import { keyframes } from '@mui/system'; // For custom animations with MUI

// Example fadeIn and fadeOut keyframes
const fadeIn = keyframes`
  0% { opacity: 0; transform: scale(0.95); }
  100% { opacity: 1; transform: scale(1); }
`;

const fadeOut = keyframes`
  0% { opacity: 1; transform: scale(1); }
  100% { opacity: 0; transform: scale(0.98); }
`;

export default function SplashScreen({ onFinish }) {
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    // Show splash for ~3 seconds, then fade out
    const timer = setTimeout(() => {
      setIsVisible(false);
      // Additional small delay for fade-out
      setTimeout(() => {
        onFinish(); // Signal to parent that splash is done
      }, 800);
    }, 3000);

    return () => clearTimeout(timer);
  }, [onFinish]);

  return (
    <Box
      sx={{
        // Full screen overlay
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        zIndex: 9999,
        // Calming pastel background, can tweak as desired
        background: 'linear-gradient(135deg, #80DEEA, #CE93D8)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        // Animate in or out
        animation: `${isVisible ? fadeIn : fadeOut} 0.8s forwards ease-in-out`,
      }}
    >
      {/* Logo */}
      <Box
        component="img"
        src="src/assets/unmute.png"  // Replace with the actual path to your logo
        alt="Unmute Logo"
        sx={{
          width: { xs: 150, sm: 200 },
          mb: 2,
        }}
      />
      {/* Tagline */}
      <Typography
        variant="h5"
        sx={{
          color: '#fff',
          textAlign: 'center',
          px: 2,
          fontWeight: 500,
        }}
      >
        Because silence shouldn't be the only option
      </Typography>
    </Box>
  );
}
