import React, { useEffect, useState } from 'react';
import { Box, Typography } from '@mui/material';
import { keyframes } from '@mui/system';
import unmuteLogo from '../assets/unmute.png';

// Container fade-out after some delay
const fadeOutContainer = keyframes`
  0% { opacity: 1; }
  100% { opacity: 0; }
`;

// Logo fade-in (with scale)
const fadeInLogo = keyframes`
  0% { opacity: 0; transform: scale(0.8); }
  100% { opacity: 1; transform: scale(1); }
`;

// Tagline fade-in (slight upward motion)
const fadeInTagline = keyframes`
  0% { opacity: 0; transform: translateY(10px); }
  100% { opacity: 1; transform: translateY(0); }
`;

export default function SplashScreen({ onFinish }) {
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    // Show the splash for ~3 seconds, then fade out
    const timer = setTimeout(() => {
      setFadeOut(true);
      // Additional delay for the fade-out animation
      setTimeout(() => {
        onFinish(); // Let parent know the splash is done
      }, 800);
    }, 3000);

    return () => clearTimeout(timer);
  }, [onFinish]);

  return (
    <Box
      sx={{
        // Full screen overlay
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        zIndex: 9999,
        // Use the exact hex from the attached logo background
        backgroundColor: '#4a90e2',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',

        // Fade out the entire container if fadeOut is true
        animation: fadeOut
          ? `${fadeOutContainer} 0.8s forwards ease-in-out`
          : 'none',
      }}
    >
      {/* Logo */}
      <Box
        component="img"
        src={unmuteLogo} // Make sure this path is correct
        alt="Unmute Logo"
        sx={{
          width: { xs: 160, sm: 220 },
          mb: 2,
          opacity: 0, // Start hidden
          animation: `${fadeInLogo} 1s forwards ease-in-out`,
        }}
      />

      {/* Tagline */}
      <Typography
        variant="h4" 
        sx={{
          fontFamily: '"Poppins", sans-serif',
          fontWeight: 600,
          color: '#fff',
          textAlign: 'center',
          px: 3,
          opacity: 0, // Start hidden
          animation: `${fadeInTagline} 1s forwards ease-in-out`,
          animationDelay: '0.7s', // Delayed start after the logo
        }}
      >
        Because silence shouldn't be the only option
      </Typography>
    </Box>
  );
}
