import React, { useEffect, useState } from 'react';
import { Box, Typography } from '@mui/material';
import { keyframes } from '@mui/system';
import unmuteLogo from '../assets/unmute.png';

// Animated background gradient
const animatedBackground = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

// Container fade-out after delay
const fadeOutContainer = keyframes`
  0% { opacity: 1; }
  100% { opacity: 0; }
`;

// Logo fade-in with a subtle pulse effect
const fadeInLogo = keyframes`
  0% { opacity: 0; transform: scale(0.8); }
  80% { opacity: 1; transform: scale(1.05); }
  100% { opacity: 1; transform: scale(1); }
`;

// Tagline fade-in with slight upward motion
const fadeInTagline = keyframes`
  0% { opacity: 0; transform: translateY(20px); }
  100% { opacity: 1; transform: translateY(0); }
`;

export default function SplashScreen({ onFinish }) {
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    // Display splash for ~3 seconds then trigger fade out
    const timer = setTimeout(() => {
      setFadeOut(true);
      // Wait for fade-out animation before calling onFinish
      setTimeout(() => {
        onFinish();
      }, 800);
    }, 3000);

    return () => clearTimeout(timer);
  }, [onFinish]);

  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        zIndex: 9999,
        background: 'linear-gradient(45deg, #FF9A9E, #FAD0C4, #A18CD1, #FBC2EB)',
        backgroundSize: '400% 400%',
        animation: `${animatedBackground} 15s ease infinite`,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        // Apply fade-out animation if needed
        ...(fadeOut && {
          animation: `${fadeOutContainer} 0.8s forwards ease-in-out`
        }),
        p: 2,
      }}
    >
      {/* Logo with enhanced animation */}
      <Box
        component="img"
        src={unmuteLogo}
        alt="Unmute Logo"
        sx={{
          width: { xs: 240, sm: 330 },
          mb: 3,
          opacity: 0,
          animation: `${fadeInLogo} 1.2s forwards ease-in-out`,
        }}
      />

      {/* Primary Tagline */}
      <Typography
        variant="h3"
        sx={{
          fontFamily: '"Comfortaa", cursive',
          fontWeight: 700,
          color: '#fff',
          textAlign: 'center',
          mb: 1,
          opacity: 0,
          animation: `${fadeInTagline} 1s forwards ease-in-out`,
          animationDelay: '0.8s',
        }}
      >
        Speak Freely, Heal Faster
      </Typography>

      {/* Secondary Tagline */}
      <Typography
        variant="h5"
        sx={{
          fontFamily: '"Comfortaa", cursive',
          fontWeight: 500,
          color: '#fff',
          textAlign: 'center',
          px: 3,
          opacity: 0,
          animation: `${fadeInTagline} 1s forwards ease-in-out`,
          animationDelay: '1s',
        }}
      >
        Because silence shouldn't be the only option.
      </Typography>
    </Box>
  );
}
