import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Button, 
  TextField, 
  Typography, 
  Box, 
  Alert,
  CircularProgress
} from '@mui/material';
import { AuthLayout } from '../../components/Auth/AuthLayout';
import { authStyles } from './authStyles';
import OTPInput from '../../components/Auth/OTPInput';
import { authService } from '../../services/auth';
import { useAuth } from '../../contexts/AuthContext';

export default function Signup() {
  const classes = authStyles();
  const navigate = useNavigate();
  const { login } = useAuth();
  const [step, setStep] = useState(1);
  const [phone, setPhone] = useState('');
  const [username, setUsername] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [resendDisabled, setResendDisabled] = useState(false);

  const handleSendOTP = async () => {
    try {
      setLoading(true);
      setError('');
      await authService.requestOTP(phone);
      setStep(2);
      startResendCooldown();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to send OTP');
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyOTP = async (enteredOtp) => {
    try {
      setLoading(true);
      setError('');
      const { data } = await authService.verifyOTP(phone, enteredOtp);

      if (data.user.randomUsernameGenerated) {
        setStep(3); // Show username setup
      } else {
        login(data.token, data.user);
        navigate('/');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Invalid OTP');
    } finally {
      setLoading(false);
    }
  };

  const handleResendOTP = async () => {
    try {
      setResendDisabled(true);
      await authService.requestOTP(phone);
      startResendCooldown();
    } catch (err) {
      setError('Failed to resend OTP');
    }
  };

  const startResendCooldown = () => {
    setResendDisabled(true);
    setTimeout(() => setResendDisabled(false), 30000); // 30-second cooldown
  };

  const handleUsernameSubmit = async () => {
    try {
      setLoading(true);
      setError('');
      const { data } = await authService.changeUsername(username);
      login(data.token, data.user);
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Username update failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <AuthLayout title={step === 1 ? "Get Started" : step === 2 ? "Verify OTP" : "Choose Username"}>
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      {step === 1 ? (
        <Box component="form" sx={classes.form}>
          <TextField
            fullWidth
            label="Phone Number"
            variant="outlined"
            value={phone}
            onChange={(e) => setPhone(e.target.value.replace(/[^0-9+]/g, ''))}
            sx={classes.input}
            placeholder="+1234567890"
            disabled={loading}
            inputProps={{ maxLength: 15 }}
          />

          <Button
            fullWidth
            variant="contained"
            size="large"
            onClick={handleSendOTP}
            disabled={loading || !phone}
            sx={classes.button}
          >
            {loading ? <CircularProgress size={24} /> : 'Send OTP'}
          </Button>
        </Box>
      ) : step === 2 ? (
        <Box sx={classes.form}>
          <OTPInput 
            length={6} 
            onComplete={handleVerifyOTP}
            disabled={loading}
          />

          <Box sx={{ 
            mt: 2, 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center',
            gap: 1
          }}>
            <Typography variant="body2">
              Didn't receive code?
            </Typography>
            <Button 
              size="small" 
              onClick={handleResendOTP}
              disabled={resendDisabled || loading}
            >
              Resend {resendDisabled && '(30s)'}
            </Button>
          </Box>
        </Box>
      ) : (
        <Box sx={classes.form}>
          <TextField
            fullWidth
            label="Choose Username"
            variant="outlined"
            value={username}
            onChange={(e) => setUsername(e.target.value.replace(/[^a-zA-Z0-9_]/g, ''))}
            sx={classes.input}
            placeholder="cool_user123"
            disabled={loading}
            inputProps={{ maxLength: 20 }}
          />

          <Button
            fullWidth
            variant="contained"
            size="large"
            onClick={handleUsernameSubmit}
            disabled={loading || username.length < 3}
            sx={classes.button}
          >
            {loading ? <CircularProgress size={24} /> : 'Complete Setup'}
          </Button>
        </Box>
      )}
    </AuthLayout>
  );
}
