import React, { useEffect, useState } from 'react';
import { Box, Typography } from '@mui/material';
import { keyframes } from '@mui/system';
import unmuteLogo from '../assets/logo.png';

/* 1) Multilayered Gradient Pulse */
const gradientPulse = keyframes`
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
`;

/* 2) Soft Logo Entrance with Glow */
const logoEntrance = keyframes`
  0% {
    opacity: 0;
    transform: translateY(100px) rotate(-5deg);
    filter: blur(10px);
  }
  60% {
    opacity: 1;
    transform: translateY(-20px) rotate(2deg);
    filter: blur(0);
  }
  80% { transform: translateY(10px) rotate(-1deg); }
  100% {
    transform: translateY(0) rotate(0);
    filter: drop-shadow(0 0 20px rgba(255, 255, 255, 0.3));
  }
`;

/* 3) Gentle Hover Float */
const float = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
`;

/* 4) Dynamic Text Reveal */
const textReveal = keyframes`
  0% {
    opacity: 0;
    letter-spacing: -0.5em;
    filter: blur(5px);
  }
  100% {
    opacity: 1;
    letter-spacing: normal;
    filter: blur(0);
  }
`;

/* 5) Organic Bubble Motion */
const bubbleFlow = keyframes`
  0% {
    transform: translate(0, 0) scale(0.8);
    opacity: 0;
  }
  20% {
    transform: translate(-10px, -20px) scale(1);
    opacity: 0.4;
  }
  60% {
    transform: translate(15px, -60px) scale(1.1);
    opacity: 0.7;
  }
  100% {
    transform: translate(0, -120vh) scale(1.3);
    opacity: 0;
  }
`;

/* 6) Particle Sparkle */
const particleSpark = keyframes`
  0% {
    transform: scale(0) rotate(0deg);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: scale(2) rotate(360deg);
    opacity: 0;
  }
`;

const BUBBLES = [
  { left: '5%', size: 40, delay: '0s', duration: '18s', color: 'rgba(255, 255, 255, 0.12)' },
  { left: '15%', size: 60, delay: '1.5s', duration: '20s', color: 'rgba(255, 255, 255, 0.18)' },
  { left: '30%', size: 55, delay: '3s', duration: '16s', color: 'rgba(255, 255, 255, 0.15)' },
  { left: '45%', size: 70, delay: '2s', duration: '22s', color: 'rgba(255, 255, 255, 0.10)' },
  { left: '60%', size: 45, delay: '4s', duration: '19s', color: 'rgba(255, 255, 255, 0.20)' },
  { left: '75%', size: 65, delay: '1s', duration: '17s', color: 'rgba(255, 255, 255, 0.14)' },
  { left: '90%', size: 50, delay: '2.5s', duration: '21s', color: 'rgba(255, 255, 255, 0.16)' },
];

const PARTICLES = Array.from({ length: 30 }).map((_, i) => ({
  size: Math.random() * 4 + 2,
  angle: Math.random() * 360,
  delay: Math.random() * 2 + 's',
  position: {
    x: Math.random() * 100 - 10 + '%',
    y: Math.random() * 100 - 10 + '%'
  }
}));

export default function SplashScreen({ onFinish }) {
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setFadeOut(true);
      setTimeout(onFinish, 1000);
    }, 5000);
    return () => clearTimeout(timer);
  }, [onFinish]);

  return (
    <Box sx={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      zIndex: 9999,
      background: `
        linear-gradient(
          135deg,
          rgba(74, 144, 226, 0.9) 0%,
          rgba(100, 181, 246, 0.8) 30%,
          rgba(174, 213, 255, 0.7) 100%
        )
      `,
      animation: `${gradientPulse} 12s ease infinite`,
      backgroundSize: '200% 200%',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      opacity: fadeOut ? 0 : 1,
      transition: 'opacity 1s ease-in-out',
    }}>
      {/* Particle Field */}
      {PARTICLES.map((particle, i) => (
        <Box key={i} sx={{
          position: 'absolute',
          left: particle.position.x,
          top: particle.position.y,
          width: particle.size,
          height: particle.size,
          backgroundColor: 'rgba(255, 255, 255, 0.4)',
          borderRadius: '50%',
          animation: `${particleSpark} 3s ease-in-out infinite`,
          animationDelay: particle.delay,
        }} />
      ))}

      {/* Animated Bubbles */}
      {BUBBLES.map((bubble, i) => (
        <Box key={i} sx={{
          position: 'absolute',
          bottom: '-100px',
          left: bubble.left,
          width: bubble.size,
          height: bubble.size,
          borderRadius: '50%',
          backgroundColor: bubble.color,
          animation: `${bubbleFlow} ${bubble.duration} cubic-bezier(0.4, 0, 0.2, 1) infinite`,
          animationDelay: bubble.delay,
          zIndex: 1,
        }} />
      ))}

      {/* Logo with Continuous Float */}
      <Box
        component="img"
        src={unmuteLogo}
        alt="Unmute Logo"
        sx={{
          width: { xs: 200, sm: 300 },
          mb: 3,
          opacity: 0,
          animation: `
            ${logoEntrance} 1.5s ease-out forwards,
            ${float} 4s ease-in-out infinite 1.5s
          `,
          zIndex: 2,
        }}
      />

      {/* Animated Text Container */}
      <Box sx={{ position: 'relative', zIndex: 2 }}>
        <Typography
          variant="h3"
          sx={{
            fontFamily: '"Comic Neue", cursive',
            fontWeight: 700,
            color: '#fff',
            textAlign: 'center',
            mb: 1,
            opacity: 0,
            animation: `
              ${textReveal} 1.2s cubic-bezier(0.34, 1.56, 0.64, 1) forwards,
              ${float} 3s ease-in-out infinite 2s
            `,
            animationDelay: '1.2s',
            textShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
          }}
        >
          Speak Freely, Heal Faster
        </Typography>

        <Typography
          variant="h5"
          sx={{
            fontFamily: '"Comic Neue", cursive',
            fontWeight: 500,
            color: 'rgba(255, 255, 255, 0.9)',
            textAlign: 'center',
            px: 3,
            opacity: 0,
            animation: `
              ${textReveal} 1s ease-out forwards,
              ${float} 3s ease-in-out infinite 2.5s
            `,
            animationDelay: '2s',
            transformOrigin: 'center top',
          }}
        >
          Where your voice finds its wings
        </Typography>
      </Box>
    </Box>
  );
}
