import React, { useEffect, useState } from 'react';
import { Box, Typography } from '@mui/material';
import { keyframes } from '@mui/system';
import unmuteLogo from '../assets/logo.png';

/* 
  1) Container Fade-Out
     Fades the entire splash screen after the set duration.
*/
const fadeOutContainer = keyframes`
  0% { opacity: 1; }
  100% { opacity: 0; }
`;

/* 
  2) Logo Bounce-In
     A fun, bouncy entrance animation for the logo.
*/
const bounceInLogo = keyframes`
  0% {
    opacity: 0;
    transform: scale(0.8) translateY(200px);
  }
  50% {
    opacity: 1;
    transform: scale(1.1) translateY(-20px);
  }
  70% {
    transform: scale(0.95) translateY(10px);
  }
  100% {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
`;

/* 
  3) Tagline Fade-In/Up
     Gently fades in text from below.
*/
const fadeInUp = keyframes`
  0% {
    opacity: 0;
    transform: translateY(30px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

/* 
  4) Floating Bubbles
     They rise slowly, fade in/out, and slightly scale 
     to convey a relaxed, mindful atmosphere.
*/
const bubbleFloat = keyframes`
  0% {
    transform: translateY(0) scale(0.9);
    opacity: 0;
  }
  20% {
    opacity: 0.3;
  }
  60% {
    opacity: 0.8;
  }
  100% {
    transform: translateY(-120vh) scale(1.1);
    opacity: 0;
  }
`;

/*
  Adjust bubble positions, sizes, colors, delays, and durations 
  to create a unique, soothing effect.
*/
const BUBBLES = [
  { left: '8%',  size: 50, delay: '0s',  duration: '14s', color: 'rgba(255, 255, 255, 0.15)' },
  { left: '20%', size: 70, delay: '2s',  duration: '16s', color: 'rgba(255, 255, 255, 0.2)'  },
  { left: '40%', size: 60, delay: '4s',  duration: '18s', color: 'rgba(255, 255, 255, 0.15)' },
  { left: '65%', size: 80, delay: '3s',  duration: '20s', color: 'rgba(255, 255, 255, 0.12)' },
  { left: '85%', size: 40, delay: '1s',  duration: '15s', color: 'rgba(255, 255, 255, 0.25)' },
];

export default function SplashScreen({ onFinish }) {
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    // Display splash for 5s, then fade out for 1s
    const timer = setTimeout(() => {
      setFadeOut(true);
      setTimeout(() => {
        onFinish();
      }, 1000);
    }, 5000);

    return () => clearTimeout(timer);
  }, [onFinish]);

  return (
    <Box
      sx={{
        // Full-screen overlay
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        zIndex: 9999,

        /* 
          Radial Gradient:
          - Start with a lighter teal in the center (#80DEEA or #72BCD4)
          - Transition to #4a90e2 outward
          Adjust the stop percentages if your logo is dark or if you want more 
          brightness in the center.
        */
        background: 'radial-gradient(circle at 50% 50%, #4a90e2 20%, #4a90e2 100%)',

        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        p: 2,

        // Fade-out animation if triggered
        ...(fadeOut && {
          animation: `${fadeOutContainer} 1s forwards ease-in-out`,
        }),
      }}
    >
      {/* Floating Bubbles (behind logo/text) */}
      {BUBBLES.map((bubble, index) => (
        <Box
          key={index}
          sx={{
            position: 'absolute',
            bottom: '-60px',
            left: bubble.left,
            width: bubble.size,
            height: bubble.size,
            borderRadius: '50%',
            backgroundColor: bubble.color,
            animation: `${bubbleFloat} ${bubble.duration} ease-out infinite`,
            animationDelay: bubble.delay,
            zIndex: 1, // behind the logo & text
          }}
        />
      ))}

      {/* Logo (above bubbles) */}
      <Box
        component="img"
        src={unmuteLogo}
        alt="Unmute Logo"
        sx={{
          width: { xs: 200, sm: 300 },
          mb: 3,
          opacity: 0,
          animation: `${bounceInLogo} 1.8s forwards ease-out`,
          zIndex: 2,
        }}
      />

      {/* Primary Tagline */}
      <Typography
        variant="h3"
        sx={{
          fontFamily: '"Comic Neue", "Comic Sans MS", cursive',
          fontWeight: 700,
          color: '#fff',
          textAlign: 'center',
          mb: 1,
          opacity: 0,
          animation: `${fadeInUp} 1.5s forwards ease-in-out`,
          animationDelay: '1.8s',
          zIndex: 2,
        }}
      >
        Speak Freely, Heal Faster
      </Typography>

      {/* Secondary Tagline */}
      <Typography
        variant="h5"
        sx={{
          fontFamily: '"Comic Neue", "Comic Sans MS", cursive',
          fontWeight: 500,
          color: '#fff',
          textAlign: 'center',
          px: 3,
          opacity: 0,
          animation: `${fadeInUp} 1.5s forwards ease-in-out`,
          animationDelay: '2.5s',
          zIndex: 2,
        }}
      >
        Because silence shouldn't be the only option.
      </Typography>
    </Box>
  );
}
