import React, { useEffect, useState } from 'react';
import { Box, Typography } from '@mui/material';
import { keyframes } from '@mui/system';
import unmuteLogo from '../assets/logo.png';

/* 
  1) Animated background gradient:
     A slow (20s) horizontal sweep between a sky-blue (#4a90e2)
     and a teal (#80DEEA), matching the logo’s palette.
*/
const animatedBackground = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

/* 2) Container fade-out after delay */
const fadeOutContainer = keyframes`
  0% { opacity: 1; }
  100% { opacity: 0; }
`;

/* 3) Logo bounce-in (with scale + vertical motion) */
const bounceInLogo = keyframes`
  0% {
    opacity: 0;
    transform: scale(0.8) translateY(200px);
  }
  50% {
    opacity: 1;
    transform: scale(1.1) translateY(-20px);
  }
  70% {
    transform: scale(0.95) translateY(10px);
  }
  100% {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
`;

/* 4) Tagline fade-in with a gentle upward slide */
const fadeInUp = keyframes`
  0% {
    opacity: 0;
    transform: translateY(30px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

export default function SplashScreen({ onFinish }) {
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    // Show splash for ~5 seconds, then fade out for 1s
    const timer = setTimeout(() => {
      setFadeOut(true);
      setTimeout(() => {
        onFinish();
      }, 1000); // Match fadeOut duration
    }, 5000);

    return () => clearTimeout(timer);
  }, [onFinish]);

  return (
    <Box
      sx={{
        // Full-screen overlay
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        zIndex: 9999,

        // Animated gradient background
        background: 'linear-gradient(120deg, #4a90e2, #80DEEA, #4a90e2)',
        backgroundSize: '200% 200%',
        animation: `${animatedBackground} 20s ease infinite`,

        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        p: 2,

        // Fade-out animation if triggered
        ...(fadeOut && {
          animation: `${fadeOutContainer} 1s forwards ease-in-out`
        }),
      }}
    >
      {/* Logo with a bouncy entrance */}
      <Box
        component="img"
        src={unmuteLogo}
        alt="Unmute Logo"
        sx={{
          width: { xs: 220, sm: 320 },
          mb: 3,
          opacity: 0,
          animation: `${bounceInLogo} 1.8s forwards ease-out`,
        }}
      />

      {/* Primary Tagline */}
      <Typography
        variant="h3"
        sx={{
          fontFamily: '"Comic Neue", "Comic Sans MS", cursive',
          fontWeight: 700,
          color: '#fff',
          textAlign: 'center',
          mb: 1,
          opacity: 0,
          animation: `${fadeInUp} 1.5s forwards ease-in-out`,
          animationDelay: '1.8s',
        }}
      >
        Speak Freely, Heal Faster
      </Typography>

      {/* Secondary Tagline */}
      <Typography
        variant="h5"
        sx={{
          fontFamily: '"Comic Neue", "Comic Sans MS", cursive',
          fontWeight: 500,
          color: '#fff',
          textAlign: 'center',
          px: 3,
          opacity: 0,
          animation: `${fadeInUp} 1.5s forwards ease-in-out`,
          animationDelay: '2.5s',
        }}
      >
        Because silence shouldn't be the only option.
      </Typography>
    </Box>
  );
}
