import React, { useState, useEffect } from 'react';
import { 
  Typography, 
  Box, 
  CircularProgress, 
  Card, 
  CardContent, 
  Avatar, 
  IconButton, 
  Dialog, 
  DialogTitle, 
  DialogContent, 
  DialogActions, 
  Button, 
  TextField,
  Switch
} from '@mui/material';
import Autocomplete from '@mui/material/Autocomplete';
import SettingsIcon from '@mui/icons-material/Settings';
import { useAuth } from '../../contexts/AuthContext';
import { authService } from '../../services/auth';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const { user, login } = useAuth();
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Settings dialog state – use arrays for multi‑value fields and separate fields for preferences
  const [openSettings, setOpenSettings] = useState(false);
  const [formUsername, setFormUsername] = useState('');
  const [formBio, setFormBio] = useState('');
  const [formInterests, setFormInterests] = useState([]);
  const [formLikes, setFormLikes] = useState([]);
  const [formDislikes, setFormDislikes] = useState([]);
  const [formMatchPreference, setFormMatchPreference] = useState('');
  const [formAnonymousChat, setFormAnonymousChat] = useState(false);
  const [saving, setSaving] = useState(false);
  const [settingsError, setSettingsError] = useState('');

  useEffect(() => {
    if (!user || !user._id) {
      navigate('/login');
      return;
    }
    const fetchUserDetails = async () => {
      try {
        const res = await authService.getUserDetails(user._id);
        if (res.data && res.data.user) {
          setUserDetails(res.data.user);
        }
      } catch (error) {
        console.error("Error fetching user details:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchUserDetails();
  }, [user, navigate]);

  const handleOpenSettings = () => {
    // Pre-fill fields with current details
    setFormUsername(userDetails.username || '');
    setFormBio(userDetails.bio || '');
    setFormInterests(
      Array.isArray(userDetails.interests)
        ? userDetails.interests
        : typeof userDetails.interests === 'string'
        ? userDetails.interests.split(',').map(item => item.trim())
        : []
    );
    setFormLikes(
      Array.isArray(userDetails.likes)
        ? userDetails.likes
        : typeof userDetails.likes === 'string'
        ? userDetails.likes.split(',').map(item => item.trim())
        : []
    );
    setFormDislikes(
      Array.isArray(userDetails.dislikes)
        ? userDetails.dislikes
        : typeof userDetails.dislikes === 'string'
        ? userDetails.dislikes.split(',').map(item => item.trim())
        : []
    );
    const prefs = userDetails.preferences || {};
    setFormMatchPreference(prefs.matchPreference || '');
    setFormAnonymousChat(!!prefs.anonymousChat);
    setSettingsError('');
    setOpenSettings(true);
  };

  const handleCloseSettings = () => {
    setOpenSettings(false);
  };

  const handleSettingsSave = async () => {
    setSaving(true);
    setSettingsError('');
    try {
      // If username changed, update it.
      if (formUsername !== userDetails.username && formUsername.trim() !== "") {
        const resUsername = await authService.changeUsername(formUsername);
        if (resUsername.data && resUsername.data.user) {
          // Here we assume the original token remains valid.
          setUserDetails(resUsername.data.user);
        }
      }
      // Prepare updated details for other fields.
      const updateData = {
        bio: formBio,
        interests: formInterests,  // already an array
        likes: formLikes,
        dislikes: formDislikes,
        preferences: {
          matchPreference: formMatchPreference,
          anonymousChat: formAnonymousChat,
        },
      };
      const resDetails = await authService.updateUserDetails(updateData);
      if (resDetails.data && resDetails.data.user) {
        // Continue using the original token.
        login(localStorage.getItem('token'), resDetails.data.user);
        setUserDetails(resDetails.data.user);
      }
      setOpenSettings(false);
    } catch (err) {
      setSettingsError(err.response?.data?.message || err.message || 'Profile update failed');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!userDetails) {
    return (
      <Typography variant="h6" align="center" sx={{ mt: 4 }}>
        No user details available.
      </Typography>
    );
  }

  return (
    <Box sx={{ p: 3, maxWidth: 800, mx: 'auto', backgroundColor: '#f3f6f9', borderRadius: 2 }}>
      <Card elevation={6} sx={{ borderRadius: '16px', p: 3, position: 'relative', backgroundColor: '#fff' }}>
        <IconButton 
          onClick={handleOpenSettings}
          sx={{ position: 'absolute', top: 16, right: 16 }}
          aria-label="settings"
        >
          <SettingsIcon fontSize="large" />
        </IconButton>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
            <Avatar 
              src={userDetails.profilePic || '/default-avatar.png'} 
              sx={{ width: 100, height: 100, mr: 3, border: '4px solid #1976d2' }}
            >
              {userDetails.username.charAt(0).toUpperCase()}
            </Avatar>
            <Typography variant="h3" sx={{ fontWeight: 600, color: '#333' }}>
              Welcome, {userDetails.username}!
            </Typography>
          </Box>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Bio
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              {userDetails.bio || 'No bio available.'}
            </Typography>
          </Box>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Interests
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              {userDetails.interests && Array.isArray(userDetails.interests)
                ? userDetails.interests.join(' • ')
                : userDetails.interests || 'Not specified.'}
            </Typography>
          </Box>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Likes
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              {userDetails.likes && Array.isArray(userDetails.likes)
                ? userDetails.likes.join(' • ')
                : userDetails.likes || 'Not specified.'}
            </Typography>
          </Box>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Dislikes
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              {userDetails.dislikes && Array.isArray(userDetails.dislikes)
                ? userDetails.dislikes.join(' • ')
                : userDetails.dislikes || 'Not specified.'}
            </Typography>
          </Box>
          <Box>
            <Typography variant="h5" color="textSecondary" sx={{ mb: 1 }}>
              Preferences
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.1rem', color: '#555' }}>
              Anonymous Chat: {userDetails.preferences?.anonymousChat ? "Yes" : "No"} <br />
              Match Preference: {userDetails.preferences?.matchPreference || 'Not specified.'}
            </Typography>
          </Box>
        </CardContent>
      </Card>

      <Dialog open={openSettings} onClose={handleCloseSettings} fullWidth maxWidth="sm">
        <DialogTitle>Update Profile</DialogTitle>
        <DialogContent>
          {settingsError && (
            <Typography color="error" variant="body2" sx={{ mb: 2 }}>
              {settingsError}
            </Typography>
          )}
          {/* Username */}
          <TextField
            margin="dense"
            label="Username"
            fullWidth
            value={formUsername}
            onChange={(e) => setFormUsername(e.target.value)}
          />
          {/* Bio */}
          <TextField
            margin="dense"
            label="Bio"
            fullWidth
            multiline
            rows={3}
            value={formBio}
            onChange={(e) => setFormBio(e.target.value)}
          />
          {/* Interests */}
          <Autocomplete
            multiple
            freeSolo
            options={[]}
            value={formInterests}
            onChange={(event, newValue) => setFormInterests(newValue)}
            renderInput={(params) => (
              <TextField {...params} margin="dense" label="Interests" fullWidth />
            )}
          />
          {/* Likes */}
          <Autocomplete
            multiple
            freeSolo
            options={[]}
            value={formLikes}
            onChange={(event, newValue) => setFormLikes(newValue)}
            renderInput={(params) => (
              <TextField {...params} margin="dense" label="Likes" fullWidth />
            )}
          />
          {/* Dislikes */}
          <Autocomplete
            multiple
            freeSolo
            options={[]}
            value={formDislikes}
            onChange={(event, newValue) => setFormDislikes(newValue)}
            renderInput={(params) => (
              <TextField {...params} margin="dense" label="Dislikes" fullWidth />
            )}
          />
          {/* Preferences */}
          <TextField
            margin="dense"
            label="Match Preference"
            fullWidth
            select
            SelectProps={{ native: true }}
            value={formMatchPreference}
            onChange={(e) => setFormMatchPreference(e.target.value)}
          >
            <option value="">Select Preference</option>
            <option value="Similar Emotions">Similar Emotions</option>
            <option value="Different Emotions">Different Emotions</option>
            <option value="Balanced">Balanced</option>
          </TextField>
          <Box sx={{ display: 'flex', alignItems: 'center', mt: 2 }}>
            <Typography variant="subtitle1">Anonymous Chat</Typography>
            <Switch
              checked={formAnonymousChat}
              onChange={(e) => setFormAnonymousChat(e.target.checked)}
              color="primary"
              sx={{ ml: 1 }}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseSettings} disabled={saving}>Cancel</Button>
          <Button onClick={handleSettingsSave} disabled={saving}>
            {saving ? <CircularProgress size={24} /> : 'Save'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
