import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Button, 
  TextField, 
  Typography, 
  Box, 
  Alert,
  CircularProgress
} from '@mui/material';
import { AuthLayout } from '../../components/Auth/AuthLayout';
import { authStyles } from './authStyles';
import OTPInput from '../../components/Auth/OTPInput';
import { authService } from '../../services/auth';
import { useAuth } from '../../contexts/AuthContext';

export default function Signup() {
  const classes = authStyles();
  const navigate = useNavigate();
  const { login } = useAuth();
  const [step, setStep] = useState(1);
  const [phone, setPhone] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [resendDisabled, setResendDisabled] = useState(false);

  // Profile details fields for new users
  const [bio, setBio] = useState('');
  const [interests, setInterests] = useState('');
  const [likes, setLikes] = useState('');
  const [dislikes, setDislikes] = useState('');
  const [preferences, setPreferences] = useState('');

  const handleSendOTP = async () => {
    console.log("Signup: handleSendOTP called with phone:", phone);
    try {
      setLoading(true);
      setError('');
      await authService.requestOTP(phone);
      setStep(2);
      startResendCooldown();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to send OTP');
      console.error("Signup: Error in handleSendOTP", err);
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyOTP = async (enteredOtp) => {
    try {
      setLoading(true);
      setError('');
      const { data } = await authService.verifyOTP(phone, enteredOtp);

      // Save token and user data regardless
      login(data.token, data.user);

      // Check the flag returned by the backend.
      // If new user, prompt to complete profile.
      if (data.isNewUser) {
        setStep(3);
      } else {
        navigate('/');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Invalid OTP');
    } finally {
      setLoading(false);
    }
  };

  const handleResendOTP = async () => {
    try {
      setResendDisabled(true);
      await authService.requestOTP(phone);
      startResendCooldown();
    } catch (err) {
      setError('Failed to resend OTP');
    }
  };

  const startResendCooldown = () => {
    setResendDisabled(true);
    setTimeout(() => setResendDisabled(false), 30000); // 30-second cooldown
  };

  const handleProfileSubmit = async () => {
    try {
      setLoading(true);
      setError('');
      // Prepare details to update
      const details = {
        bio,
        interests,    // e.g. comma-separated values
        likes,        // e.g. comma-separated values
        dislikes,     // e.g. comma-separated values
        preferences   // e.g. comma-separated values
      };
      const { data } = await authService.updateUserDetails(details);
      // Optionally update the auth context with the new user data
      login(data.token, data.user);
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Profile update failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <AuthLayout title={step === 1 ? "Get Started" : step === 2 ? "Verify OTP" : "Complete Your Profile"}>
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      {step === 1 && (
        <Box component="form" sx={classes.form}>
          <TextField
            fullWidth
            label="Phone Number"
            variant="outlined"
            value={phone}
            onChange={(e) => setPhone(e.target.value.replace(/[^0-9+]/g, ''))}
            sx={classes.input}
            placeholder="+1234567890"
            disabled={loading}
            inputProps={{ maxLength: 15 }}
          />
          <Button
            fullWidth
            variant="contained"
            size="large"
            onClick={handleSendOTP}
            disabled={loading || !phone}
            sx={classes.button}
          >
            {loading ? <CircularProgress size={24} /> : 'Send OTP'}
          </Button>
        </Box>
      )}

      {step === 2 && (
        <Box sx={classes.form}>
          <OTPInput 
            length={6} 
            onComplete={handleVerifyOTP}
            disabled={loading}
          />
          <Box sx={{ 
            mt: 2, 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center',
            gap: 1
          }}>
            <Typography variant="body2">
              Didn't receive code?
            </Typography>
            <Button 
              size="small" 
              onClick={handleResendOTP}
              disabled={resendDisabled || loading}
            >
              Resend {resendDisabled && '(30s)'}
            </Button>
          </Box>
        </Box>
      )}

      {step === 3 && (
        <Box component="form" sx={classes.form}>
          <Typography variant="h6" sx={{ mb: 2 }}>Complete Your Profile</Typography>
          <TextField
            fullWidth
            label="Bio"
            variant="outlined"
            value={bio}
            onChange={(e) => setBio(e.target.value)}
            sx={classes.input}
            placeholder="Tell us about yourself"
            disabled={loading}
            multiline
            rows={3}
          />
          <TextField
            fullWidth
            label="Interests"
            variant="outlined"
            value={interests}
            onChange={(e) => setInterests(e.target.value)}
            sx={classes.input}
            placeholder="e.g. sports, music, tech"
            disabled={loading}
          />
          <TextField
            fullWidth
            label="Likes"
            variant="outlined"
            value={likes}
            onChange={(e) => setLikes(e.target.value)}
            sx={classes.input}
            placeholder="What do you like?"
            disabled={loading}
          />
          <TextField
            fullWidth
            label="Dislikes"
            variant="outlined"
            value={dislikes}
            onChange={(e) => setDislikes(e.target.value)}
            sx={classes.input}
            placeholder="What do you dislike?"
            disabled={loading}
          />
          <TextField
            fullWidth
            label="Preferences"
            variant="outlined"
            value={preferences}
            onChange={(e) => setPreferences(e.target.value)}
            sx={classes.input}
            placeholder="Your preferences (e.g. communication style)"
            disabled={loading}
          />
          <Button
            fullWidth
            variant="contained"
            size="large"
            onClick={handleProfileSubmit}
            disabled={loading}
            sx={classes.button}
          >
            {loading ? <CircularProgress size={24} /> : 'Complete Profile'}
          </Button>
        </Box>
      )}
    </AuthLayout>
  );
}
