// src/pages/Vent/VentFeed.jsx
import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Card, 
  CardContent, 
  Typography, 
  TextField, 
  Button, 
  CircularProgress, 
  IconButton, 
  Grid, 
  Alert, 
  InputAdornment,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  MenuItem,
  Chip
} from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import SentimentVerySatisfiedIcon from '@mui/icons-material/SentimentVerySatisfied';
import HearingIcon from '@mui/icons-material/Hearing';
import SearchIcon from '@mui/icons-material/Search';
import ReportIcon from '@mui/icons-material/Report';
import { useNavigate } from 'react-router-dom';
import { ventService } from '../../services/vent';
import { useAuth } from '../../contexts/AuthContext';

export default function VentFeed() {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [vents, setVents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [sortType, setSortType] = useState('recent'); // 'recent' or 'trending'
  const [searchQuery, setSearchQuery] = useState('');

  // Report Dialog state
  const [reportOpen, setReportOpen] = useState(false);
  const [reportVentId, setReportVentId] = useState('');
  const [reportReason, setReportReason] = useState('');
  const [reportOtherText, setReportOtherText] = useState('');
  const [reportLoading, setReportLoading] = useState(false);
  const [reportError, setReportError] = useState('');

  // Typical reasons for reporting a vent
  const reportReasons = [
    'Inappropriate Content',
    'Harassment',
    'Spam',
    'Other'
  ];

  useEffect(() => {
    fetchVents();
  }, [sortType]);

  const fetchVents = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await ventService.getVents({ sort: sortType, page: 1, limit: 20 });
      if (res.data && res.data.vents) {
        setVents(res.data.vents);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to fetch vents');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      fetchVents();
      return;
    }
    setLoading(true);
    setError('');
    try {
      const res = await ventService.searchVents({ query: searchQuery });
      if (res.data && res.data.vents) {
        setVents(res.data.vents);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Search failed');
    } finally {
      setLoading(false);
    }
  };

  const handleReaction = async (ventId, reactionType) => {
    try {
      await ventService.reactToVent({ ventId, reactionType });
      fetchVents(); // refresh vent list to update reaction counts
    } catch (err) {
      console.error("Error reacting to vent:", err);
    }
  };

  const openReportDialog = (ventId) => {
    setReportVentId(ventId);
    setReportReason('');
    setReportOtherText('');
    setReportError('');
    setReportOpen(true);
  };

  const closeReportDialog = () => {
    setReportOpen(false);
  };

  const handleReport = async () => {
    if (!reportReason) {
      setReportError("Please select a reason for reporting.");
      return;
    }
    // If "Other" is selected, append the additional text (if provided)
    const finalReason = reportReason === 'Other' 
      ? reportOtherText.trim() ? `Other: ${reportOtherText}` : 'Other'
      : reportReason;

    setReportLoading(true);
    setReportError('');
    try {
      await ventService.reportVent({ ventId: reportVentId, reason: finalReason });
      alert("Vent reported successfully.");
      setReportOpen(false);
    } catch (err) {
      setReportError(err.response?.data?.message || 'Failed to report vent.');
    } finally {
      setReportLoading(false);
    }
  };

  return (
    <Box sx={{ p: 2 }}>
      {/* Top Bar: Create Vent, Search & Filters */}
      <Box 
        sx={{ 
          display: 'flex', 
          flexDirection: { xs: 'column', md: 'row' }, 
          alignItems: 'center', 
          justifyContent: 'space-between', 
          mb: 3, 
          gap: 2 
        }}
      >
        <Button 
          variant="contained" 
          onClick={() => navigate('/vent/create')} 
          sx={{ backgroundColor: '#4a90e2' }}
        >
          Create Vent
        </Button>
        <TextField 
          variant="outlined" 
          placeholder="Search vents..." 
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          size="small"
          sx={{ flexGrow: 1, maxWidth: { xs: '100%', md: '300px' } }}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton onClick={handleSearch}>
                  <SearchIcon />
                </IconButton>
              </InputAdornment>
            )
          }}
        />
        <Box>
          <Button 
            variant={sortType === 'recent' ? 'contained' : 'outlined'} 
            onClick={() => setSortType('recent')}
            sx={{ mr: 1 }}
          >
            Recent
          </Button>
          <Button 
            variant={sortType === 'trending' ? 'contained' : 'outlined'} 
            onClick={() => setSortType('trending')}
          >
            Trending
          </Button>
        </Box>
      </Box>

      {/* Vent Feed */}
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : error ? (
        <Alert severity="error">{error}</Alert>
      ) : (
        <Grid container spacing={2}>
          {vents.map((vent) => (
            <Grid item xs={12} key={vent._id}>
              <Card 
                sx={{ 
                  borderRadius: '16px', 
                  p: 2, 
                  cursor: 'pointer',
                  position: 'relative'
                }}
                onClick={() => navigate(`/vent/${vent._id}`)}
              >
                <CardContent>
                  <Typography variant="h5" sx={{ fontWeight: 600, mb: 1 }}>
                    {vent.title}
                  </Typography>
                  <Typography variant="body1" sx={{ mb: 1 }}>
                    {vent.text}
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>Emotion:</strong> {vent.emotion}
                  </Typography>
                  {vent.hashtags && vent.hashtags.length > 0 && (
                    <Box sx={{ display: 'flex', gap: 1, mb: 1, flexWrap: 'wrap' }}>
                      {vent.hashtags.map((tag, index) => (
                        <Chip key={index} label={`#${tag}`} size="small" sx={{ backgroundColor: '#E0F7FA', color: '#333' }} />
                      ))}
                    </Box>
                  )}
                  <Typography variant="caption" color="textSecondary" sx={{ display: 'block', mb: 1 }}>
                    {new Date(vent.createdAt).toLocaleString()}
                  </Typography>
                  {/* Reaction Buttons */}
                  <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                    <IconButton onClick={() => handleReaction(vent._id, 'heart')} size="small">
                      <FavoriteIcon color="error" />
                      <Typography variant="caption" sx={{ ml: 0.5 }}>
                        {vent.reactions?.heart || 0}
                      </Typography>
                    </IconButton>
                    <IconButton onClick={() => handleReaction(vent._id, 'hug')} size="small">
                      <SentimentVerySatisfiedIcon color="primary" />
                      <Typography variant="caption" sx={{ ml: 0.5 }}>
                        {vent.reactions?.hug || 0}
                      </Typography>
                    </IconButton>
                    <IconButton onClick={() => handleReaction(vent._id, 'listen')} size="small">
                      <HearingIcon color="action" />
                      <Typography variant="caption" sx={{ ml: 0.5 }}>
                        {vent.reactions?.listen || 0}
                      </Typography>
                    </IconButton>
                  </Box>
                  {/* Report Button (for vents not by the current user) */}
                  {user._id !== vent.userId && (
                    <Button 
                      variant="text" 
                      color="warning" 
                      startIcon={<ReportIcon />} 
                      sx={{ position: 'absolute', top: 8, right: 8 }}
                      onClick={(e) => { 
                        e.stopPropagation(); 
                        openReportDialog(vent._id); 
                      }}
                    >
                      Report
                    </Button>
                  )}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      {/* Side Panel / My Vents Button */}
      <Box sx={{ mt: 3, textAlign: 'center' }}>
        <Button 
          variant="outlined" 
          onClick={() => navigate('/myvents')}
          sx={{ textTransform: 'none' }}
        >
          View My Vents
        </Button>
      </Box>

      {/* Report Dialog */}
      <Dialog open={reportOpen} onClose={closeReportDialog} fullWidth maxWidth="sm">
        <DialogTitle>Report Vent</DialogTitle>
        <DialogContent>
          {reportError && <Alert severity="error" sx={{ mb: 2 }}>{reportError}</Alert>}
          <TextField
            select
            fullWidth
            label="Reason"
            variant="outlined"
            value={reportReason}
            onChange={(e) => setReportReason(e.target.value)}
            sx={{ mb: 2 }}
          >
            {reportReasons.map((option) => (
              <MenuItem key={option} value={option}>
                {option}
              </MenuItem>
            ))}
          </TextField>
          {reportReason === 'Other' && (
            <TextField
              fullWidth
              label="Additional Details"
              variant="outlined"
              value={reportOtherText}
              onChange={(e) => setReportOtherText(e.target.value)}
              multiline
              rows={3}
              sx={{ mb: 2 }}
            />
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={closeReportDialog} disabled={reportLoading}>
            Cancel
          </Button>
          <Button onClick={handleReport} variant="contained" disabled={reportLoading}>
            {reportLoading ? <CircularProgress size={24} /> : 'Submit Report'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
