import React, { useEffect, useState } from 'react';
import { Box, Typography } from '@mui/material';
import { keyframes } from '@mui/system';
import unmuteLogo from '../assets/logo.png';

/* --- 1) Background Gradient Animation ---
   A slow (20s) horizontal sweep primarily using #4a90e2,
   complemented by #80DEEA for a subtle color shift. 
*/
const animatedBackground = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

/* --- 2) Container Fade-Out ---
   Fade the entire screen after the splash duration.
*/
const fadeOutContainer = keyframes`
  0% { opacity: 1; }
  100% { opacity: 0; }
`;

/* --- 3) Logo Bounce-In ---
   A bouncy, fun entrance for the logo.
*/
const bounceInLogo = keyframes`
  0% {
    opacity: 0;
    transform: scale(0.8) translateY(200px);
  }
  50% {
    opacity: 1;
    transform: scale(1.1) translateY(-20px);
  }
  70% {
    transform: scale(0.95) translateY(10px);
  }
  100% {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
`;

/* --- 4) Tagline Fade-In/Up ---
   Gently fades in text from below.
*/
const fadeInUp = keyframes`
  0% {
    opacity: 0;
    transform: translateY(30px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

/* --- 5) Floating Bubbles ---
   Move upward, fade in/out, and slightly scale.
*/
const bubbleFloat = keyframes`
  0% {
    transform: translateY(0) scale(0.8);
    opacity: 0;
  }
  20% {
    opacity: 0.3;
  }
  50% {
    opacity: 0.7;
  }
  100% {
    transform: translateY(-120vh) scale(1.3);
    opacity: 0;
  }
`;

// Customize as many bubbles as you like:
const BUBBLES = [
  { left: '10%', size: 40,  delay: '0s',  duration: '10s', color: 'rgba(255,255,255,0.2)' },
  { left: '25%', size: 60,  delay: '2s',  duration: '12s', color: 'rgba(255,255,255,0.25)' },
  { left: '50%', size: 50,  delay: '4s',  duration: '11s', color: 'rgba(255,255,255,0.2)' },
  { left: '75%', size: 70,  delay: '1s',  duration: '13s', color: 'rgba(255,255,255,0.15)' },
  { left: '90%', size: 30,  delay: '3s',  duration: '9s',  color: 'rgba(255,255,255,0.3)' },
];

export default function SplashScreen({ onFinish }) {
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    // Show splash for ~5 seconds, then fade out for 1 second
    const timer = setTimeout(() => {
      setFadeOut(true);
      setTimeout(() => {
        onFinish();
      }, 1000);
    }, 5000);

    return () => clearTimeout(timer);
  }, [onFinish]);

  return (
    <Box
      sx={{
        // Full-screen overlay
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        zIndex: 9999,

        // Animated gradient background using #4a90e2
        background: 'linear-gradient(120deg, #4a90e2, #80DEEA, #4a90e2)',
        backgroundSize: '200% 200%',
        animation: `${animatedBackground} 20s ease infinite`,

        // Center content
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        p: 2,

        // Fade-out animation if triggered
        ...(fadeOut && {
          animation: `${fadeOutContainer} 1s forwards ease-in-out`,
        }),
      }}
    >
      {/* Render floating bubbles behind content */}
      {BUBBLES.map((bubble, index) => (
        <Box
          key={index}
          sx={{
            position: 'absolute',
            bottom: '-50px',
            left: bubble.left,
            width: bubble.size,
            height: bubble.size,
            borderRadius: '50%',
            backgroundColor: bubble.color,
            animation: `${bubbleFloat} ${bubble.duration} ease-in infinite`,
            animationDelay: bubble.delay,
            zIndex: 1, // Keep them behind the logo & text
          }}
        />
      ))}

      {/* Logo (above the bubbles) */}
      <Box
        component="img"
        src={unmuteLogo}
        alt="Unmute Logo"
        sx={{
          width: { xs: 220, sm: 320 },
          mb: 3,
          opacity: 0,
          animation: `${bounceInLogo} 1.8s forwards ease-out`,
          zIndex: 2,
        }}
      />

      {/* Primary Tagline */}
      <Typography
        variant="h3"
        sx={{
          fontFamily: '"Comic Neue", "Comic Sans MS", cursive',
          fontWeight: 700,
          color: '#fff',
          textAlign: 'center',
          mb: 1,
          opacity: 0,
          animation: `${fadeInUp} 1.5s forwards ease-in-out`,
          animationDelay: '1.8s',
          zIndex: 2,
        }}
      >
        Speak Freely, Heal Faster
      </Typography>

      {/* Secondary Tagline */}
      <Typography
        variant="h5"
        sx={{
          fontFamily: '"Comic Neue", "Comic Sans MS", cursive',
          fontWeight: 500,
          color: '#fff',
          textAlign: 'center',
          px: 3,
          opacity: 0,
          animation: `${fadeInUp} 1.5s forwards ease-in-out`,
          animationDelay: '2.5s',
          zIndex: 2,
        }}
      >
        Because silence shouldn't be the only option.
      </Typography>
    </Box>
  );
}
